var tools = {
  add: (...numbers) => {
    let sum = 0
    for(let item in numbers) {
      sum += numbers[item]
    }

    return sum
  },
  multiply: (...numbers) => {
    let sum = numbers[0];
    for (let item in numbers) {
      sum = sum * numbers[item];
    }
    return sum;
  }
}

module.exports = tools;
